import React, { Component } from 'react'

export default class LegendDisplay extends Component {
    constructor(props){
        super(props)

        this.toggleActive = this.toggleActive.bind(this);
    }

    toggleActive(country){
        this.props.toggleActive(country)
    }

    render() {
        return (
            <div>
                {this.props.countries.map( (el, index) => <div key={index} onClick={toggleActive}>{el}</div>)}
            </div>
        )
    }
}
