import React, { Component } from 'react'
import * as d3 from 'd3'
import * as ReactFauxDOM from 'react-faux-dom'
import data from "../Resources/data.json"

class Chart extends Component {
    constructor(props){
        super(props);
        this.state = {
        }

        this.makeD3 = this.makeD3.bind(this);
    }

    makeD3(){
        let fauxDiv = ReactFauxDOM.createElement('div');  

        // set the dimensions and margins of the graph
        var margin = {top: 20, right: 20, bottom: 30, left: 50},
        width = 960 - margin.left - margin.right,
        height = 500 - margin.top - margin.bottom;

        // parse the date / time
        var parseTime = d3.timeParse("%Y");
        // set the ranges
        var x = d3.scaleTime().rangeRound([0, width]);
        var y = d3.scaleLinear().range([height, 0]);

        let canada = data['Canada']["Both"];

        // define the line
        var valueline = d3.line()
                            .x(function(d) { return x(d.date); })
                            .y(function(d) { return y(d.close); });

        // append the svg obgect to the body of the page
        // appends a 'group' element to 'svg'
        // moves the 'group' element to the top left margin
        var svg = d3.select(fauxDiv).append("svg")
                    .attr("width", width + margin.left + margin.right)
                    .attr("height", height + margin.top + margin.bottom)
                    .append("g")
                    .attr("transform",
                        "translate(" + margin.left + "," + margin.top + ")");

        let parseYear = d3.timeParse("%Y");

        // Scale the range of the data
        x.domain(d3.extent(canada, function(d) { return parseYear(d.year) }));
        y.domain([0, d3.max(canada, function(d) { return d.val; })]);

        svg.append("path")
            .data([canada])
            .attr("class", "line")
            .attr("d", valueline);

        // Add the X Axis
        svg.append("g")
            .attr("transform", "translate(0," + height + ")")
            .call(d3.axisBottom(x).ticks(5));

        // Add the Y Axis
        svg.append("g")
        .call(d3.axisLeft(y).ticks(10));

        console.log(canada)
        return fauxDiv;
    }



    render() {
        let FauxSvg = this.makeD3();

        return (
            FauxSvg.toReact()
        );
    }
}


export default ReactFauxDOM.withFauxDOM(Chart);

