import React, { Component } from 'react'
import * as d3 from 'd3'
import * as ReactFauxDOM from 'react-faux-dom'
class Chart extends Component {
  constructor(props){
      super(props);
      this.state = {
      }

      this.makeD3 = this.makeD3.bind(this);
  }
     makeD3(){
        let fauxDiv = ReactFauxDOM.createElement('div');  

        // Set the margins
        var margin = {top: 60, right: 100, bottom: 20, left: 80},
        width = 850 - margin.left - margin.right,
        height = 370 - margin.top - margin.bottom;

        // Parse the month variable
        var parseMonth = d3.timeParse("%b");
        var formatMonth = d3.timeFormat("%b");

        // Set the ranges
        var x = d3.scaleTime().domain([parseMonth("Jan"),parseMonth("Dec")]).range([0, width]);
        var y = d3.scaleLinear().range([height, 0]);

        // Define the line
        var valueLine = d3.line()
        .x(function(d) { return x(d.Month); })
        .y(function(d) { return y(+d.Sales); })

        // Create the svg canvas in the "graph" div
        var svg = fauxDiv.append("svg")
            .style("width", width + margin.left + margin.right + "px")
            .style("height", height + margin.top + margin.bottom + "px")
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform","translate(" + margin.left + "," + margin.top + ")")
            .attr("class", "svg");

        // Import the CSV data
        d3.csv("Example1.csv", function(error, data) {
        if (error) throw error;

        // Format the data
        data.forEach(function(d) {
            d.Month = parseMonth(d.Month);
            d.Sales = +d.Sales;
        });

        // Scale the range of the data
        x.domain(d3.extent(data, function(d) { return d.Month; }));
        y.domain([0, d3.max(data, function(d) { return d.Sales; })]);

        // Set up the x axis
        var xaxis = svg.append("g")
            .attr("transform", "translate(0," + height + ")")
            .attr("class", "x axis")
            .call(d3.axisBottom(x)
                .ticks(d3.timeMonth)
                .tickSize(0, 0)
                .tickFormat(d3.timeFormat("%B"))
                .tickSizeInner(0)
                .tickPadding(10));

        // Add the Y Axis
        var yaxis = svg.append("g")
            .attr("class", "y axis")
            .call(d3.axisLeft(y)
                .ticks(5)
                .tickSizeInner(0)
                .tickPadding(6)
                .tickSize(0, 0));

        // yaxis.select(".domain").style("display","none")

        // Add a label to the y axis
        svg.append("text")
            .attr("transform", "rotate(-90)")
            .attr("y", 0 - 60)
            .attr("x", 0 - (height / 2))
            .attr("dy", "1em")
            .style("text-anchor", "middle")
            .text("Monthly Sales")
            .attr("class", "y axis label");

        // Draw the line
        svg.append("path")
            .data([data])
            .attr("class", "line")
            .attr("d", valueLine);

            console.log(data)
            console.log([data])


        })
        return fauxDiv;
    }

    render() {
        let FauxSvg = this.makeD3();

        return (
            FauxSvg.toReact()
        );
    }
}


export default ReactFauxDOM.withFauxDOM(Chart);
