import React from 'react';

const CountryList = (props) => {
    const {active, isSearching} = props;

    const toggleActive =(country) =>{
        this.props.toggleActive(country);
    }
    if(isSearching){
        return (
            <div>
                <div className="legendActive">
                    {this.props.activeCountries.map( (el, index) => {
                        let name = el.name;
                        return(
                            <div key={index} onClick={() => {toggleActive(name) }}>{name}</div>
                        )
                    })}
                    <hr />
                </div>
            </div>
        )
    } 
    return null
};

export default CountryList;
