import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import * as d3 from 'd3'
import data from "./Resources/data.json"
import Legend from './Components/Legend/Legend'
import Chart from "./Components/Chart"
import "./Components/Chart/Chart.css"
class App extends Component {
  constructor(props){
    super(props)

    this.state = {
        activeCountries : ["Canada", "United States"],
        totalCountries : Object.keys(data),
    }

    this.removeActive = this.removeActive.bind(this)
    this.addActive = this.addActive.bind(this);
  }

  removeActive(country){
    let activeCountries = this.state.activeCountries.filter(elem => elem != country)
    this.setState( {activeCountries,} )
  }

  addActive(country){
    this.setState({activeCountries: this.state.activeCountries.append(country)})
  }

  render() {
    return (
      <div className="App">
        <div className="Chart">
          <Chart data={this.state.data}/>
        </div>

        <Legend countries={this.state.totalCountries}
                activeCountries={this.state.activeCountries}
                remove={this.removeActive}
                add={this.addActive}/>
      </div>
    );
  }
}

export default App;
