import React, { Component } from 'react'
const blocks = ["--", "EU (European Union)", "EFTA (European Free Trade Association)", "AU (African Union)", "USAN (Union of South American Nations)", "AL (Arab League)", "ASEAN (Association of Southeast Asian Nations)", "PA (Pacific Alliance)", "USAN (Union of South American Nations)"]

export default class CountryBlock extends Component {
  constructor(props){
      super(props);

      this.state = {
          selected:"--",
      }

      this.onSelect = this.onSelect.bind(this);
  }

  onSelect(event){
    let { selectBlock } = this.props;
    fetch("https://restcountries.eu/rest/v2/regionalbloc/" + event.target.value.split(" ")[0])
        .then(function(response) {
            return response.json();
        })
        .then(function(json) {
            selectBlock(json.map(el => el.name));
        });
    this.setState({selected: event.target.value});
  }

  render() {
    return (
        <div>
            <select value={this.state.selected} onChange={this.onSelect}>
                {blocks.map((elem, index) => <option value={elem} key={index}>{elem}</option> )}
            </select>
        </div>
    )
  }
}
