import { Component } from 'react'
import * as d3 from 'd3'
import * as ReactFauxDOM from 'react-faux-dom'
import data from "../Resources/data.json"

const getDataFor = (country) =>{
    return data[[country]]['Both']
}
class Chart extends Component {
    constructor(props){
        super(props);
        console.log(data);
        this.state = {
            activeCountries : ["Canada", "United States", "Kansas"],
            totalCountries : Object.keys(data),
        }

        this.makeD3 = this.makeD3.bind(this);
    }

    makeD3(){
        let fauxDiv = ReactFauxDOM.createElement('div');  

        // set the dimensions and margins of the graph
        var margin = {top: 20, right: 20, bottom: 30, left: 50},
        width = 960 - margin.left - margin.right,
        height = 500 - margin.top - margin.bottom;

        // parse the date / time
        var parseTime = d3.timeParse("%Y");
        // set the ranges
        var x = d3.scaleTime().range([0, width]);
        var y = d3.scaleLinear().range([height, 0]);

        //let canada = [{val:0.2, year:1995}, {val:1.0, year:2015}]
        let canada = data['Canada']["Both"];
        canada.sort(function(a, b){return a.year - b.year})

        let svg = d3.select(fauxDiv).append("svg")
                    .attr("width", width + margin.left + margin.right)
                    .attr("height", height + margin.top + margin.bottom)
                    .append("g")
                    .attr("transform",
                        "translate(" + margin.left + "," + margin.top + ")");

        let parseYear = d3.timeParse("%Y");

        console.log(d3.extent(canada, function(d) { return parseYear(d.year) }));
        // Scale the range of the data
        x.domain(d3.extent(canada, function(d) { return parseYear(d.year) }));
        y.domain([0, d3.max(canada, function(d) { return d.val; })]);
        window.x = x;
        window.d3 = d3;
        // define the line
        let valueline = d3.line()
            .x(function(d) { return x(new Date(d.year, 1, 1)); })
            .y(function(d) { return y(d.val); });
        for( let i = 0; i <this.state.activeCountries.length;i++){
            svg.append("path")
                .datum(canada)
                .attr("class", "line")
                .attr("d",valueline)
                .attr("stroke-width", "1px")
                .attr("stroke", 'black')
                .attr('color', 'black')
                .attr("fill", 'none');
        }

        // Add the X Axis
        svg.append("g")
            .attr("transform", "translate(0," + height + ")")
            .call(d3.axisBottom(x).ticks(5).tickSizeOuter(0));

        // Add the Y Axis
        svg.append("g")
        .call(d3.axisLeft(y).ticks(10).tickSizeOuter(0));

        return fauxDiv;
    }



    render() {
        let FauxSvg = this.makeD3();

        return (
            FauxSvg.toReact()
        );
    }
}


export default ReactFauxDOM.withFauxDOM(Chart);

