import React from 'react';

const Active = (props) => {
    const {active, isSearching, activeCountries} = props;
    const toggleActive =(country) => { this.props.toggleActive(country);}

    if(activeCountries.length && !isSearching){
        return (
            <div className="legendActive"> 
                {activeCountries.map( (el, index) => { return(<div key={index} onClick={() => {toggleActive(el.name) }}>{el.name}</div>) })}

                <hr />
            </div>
        )
    } 
    return null;
};

export default Active;
