import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import * as d3 from 'd3'
import data from "./Resources/data.json"
import Legend from './Components/Legend/Legend'
import Chart from "./Components/Chart"
import "./Components/Chart/Chart.css"

const createActive = (countries) => {
  let d = []
  for( let country in Object.keys(countries)){
    d.push({name:country, active:false})
  }
  window.d = d;
  return d
}

class App extends Component {
  constructor(props){
    super(props)

    this.state = {
        countries : createActive(Object.keys(data)),
    }

    this.toggleActive = this.toggleActive.bind(this)
  }

  toggleActive(country){
    let countries = this.state.countries;
    let currentCountryState = countries[ countries.findIndex(elem => elem.name == country) ]
    let toggledActive = !currentCountryState.active
    countries.push({name:country, active:toggledActive})
    this.setState({countries: countries})
  }

  render() {
    console.log(this.state.countries);
    return (
      <div className="App">
        <div className="Chart-Wrapper">
          <Chart data={this.state.data}/>
          <Legend countries={this.state.countries}
                  activeCountries={this.state.activeCountries}
                  toggleActive={this.toggleActive}/>
        </div>
      </div>
    );
  }
}

export default App;
