import React, { Component } from 'react'


const getActive = (countries) => {
    return countries.filter(el => el.active === true)
}

export default class LegendDisplay extends Component {
    constructor(props){
        super(props)

        this.state = {
            isSearching: false
        }

        this.toggleActive = this.toggleActive.bind(this);
        this.isSearching = this.isSearching.bind(this);
    }

    toggleActive(country){
        console.log('display', country);
        this.props.toggleActive(country)
    }

    isSearching(){
        this.setState({isSearching: ! this.state.isSearching})
    }

    render() {
        let { isSearching } = this.state;
        let { countries } = this.props;
        let activeCountries = getActive(countries);
        return (
            <div>
                {activeCountries.length && !isSearching
                ?  <div className="legendActive">
                        {activeCountries.map( (el, index) => {
                            let name = el.name;
                            return(
                                <div key={index} onClick={() => { this.toggleActive(name) }}>{name}</div>
                            )

                        })}
                        <hr />
                    </div>
                :null }
                <div className="legendSelector">
                    {this.props.countries.map( (el, index) => {
                        let name = el.name;
                        return(
                            <div key={index} onClick={() => { this.toggleActive(name) }}>{name}</div>
                        )
                    })}
                </div>
            </div>
        )
    }
}
